parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "operation": {
      "type": "string",
      "description": "A operação a ser realizada: 'create', 'update', 'delete' ou 'fetch'",
      "enum": ["create", "update", "delete", "fetch"],
      "example": "create"
    },
    "userQuery": {
      "type": "string",
      "description": "Data e hora do agendamento solicitada pelo usuário",
      "example": "sexta-feira às 17h; amanhã às 18h"
    },
    "userPhone": {
      "type": "string",
      "description": "Telefone do usuário (opcional, mas pode ser útil)",
      "example": "+5511999998888"
    },
    "userName": {
      "type": "string",
      "description": "Nome do usuário que está agendando",
      "example": "Leonardo"
    }
  },
  "required": ["operation", "userQuery", "userName"]
}
